sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var mypass = current.u_string_password;
    var body;
    var status;

    try {
        var ws = new sn_ws.RESTMessageV2();
        mypass = mypass.getDecryptedValue();



        ws.setEndpoint('http://10.0.9.141:5000/validate?username=' + current.u_string_username + "&password=" + mypass);

        ws.setHttpMethod('get');

        // 		ws.setMIDServer(current.mid_server.name);
        ws.setMIDServer('My_Linux_Mid_Server_Nikunj1');
        ws.setEccParameter('skip_sensor', 'true');

        var response = ws.execute();
        body = response.haveError() ? response.getErrorMessage() : response.getBody();
		if(body == "Valid"){
			gs.addInfoMessage("Valid User");
		}else{
			gs.addErrorMessage("Invalid User");
		}
        gs.info("Response: Status:" + response.getStatusCode() + " Username: " + current.u_string_username + " Password: " + mypass + " Body: " + body);
        status = response.getStatusCode();
    } catch (ex) {
        gs.error("Exception" + ex);
        current.setAbortAction(true);
    }
})(current, previous);
